mapper.select:
SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT board_num, /* 검색페이지 내에서 html을 허용 하지 않기위해 sql문에서 설정해야됨 */
REPLACE(REPLACE(title,'<','&lt;'),'>','&gt;') title,
hit, reg_date, mem_num, id, nick_name, re_cnt, fav_cnt /* 좋아요 없으면 null이됨 nulls last를 위쪽 boardOrder에 명시*/ FROM spboard /* (SELECT~ 인라인뷰형태) table 생성 */ LEFT OUTER JOIN (SELECT COUNT (*) re_cnt, board_num FROM spboard_reply GROUP BY board_num) USING(board_num) LEFT OUTER JOIN (SELECT COUNT (*) fav_cnt, board_num FROM spboard_fav GROUP BY board_num) USING(board_num) JOIN spmember USING(mem_num)
)a)
WHERE rnum >= #{start} AND rnum <= #{end}

mapper.update:
UPDATE spboard SET
filename=#{filename},
title=#{title}, content=#{content}, ip=#{ip}, modify_date=SYSDATE WHERE board_num=#{board_num}
